package com.biz.stringarray;

public class StrArray_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String[] strName = {"홍","길","동"};
		for(int i = 0 ; i < 3 ; i++) {
			System.out.print(strName[i]);
		}
		
		System.out.println(strName[3]);
		
		String[] strNation = {"대","한","민","국"};
		for(int i = 0 ; i < 4 ; i++) {
			System.out.print(strNation[i]);
		}
		
		
		
		
	}

}
